let heroSwiper,clientsSwiper,tabsSwiper;function initHeroSwiper(){heroSwiper=new Swiper(".hero-swiper__swiper",{slidesPerView:1,loop:!0,speed:1500,effect:"fade",fadeEffect:{crossFade:!0},autoplay:{delay:3e3,disableOnInteraction:!1}})}function initClientsSwiper(){clientsSwiper=new Swiper(".clients-swiper__swiper",{loop:!0,slidesPerView:"auto",spaceBetween:0,centeredSlides:!0,speed:6500,autoplay:{delay:1,disableOnInteraction:!1},allowTouchMove:!1,disableOnInteraction:!0})}function initTabsSwiper(){tabsSwiper=new Swiper(".tabs-swiper__swiper",{loop:!0,slidesPerView:1,effect:"fade",fadeEffect:{crossFade:!0},parallax:!0,autoplay:!1,spaceBetween:0,autoHeight:!0,speed:800,simulateTouch:!1,pagination:{el:".tabs-swiper__pagination",clickable:!0,renderBullet:function(e,i){return`<span class="${i}">Page no.${e+1}</span>`}}})}